mapper.select:
select username, userid, email
from member
where userid = #{userid} and userpwd = #{userpwd}

select userid, userpwd, username, email, phone,date_format(joindate, '%y.%m.%d') joindate
from member
where userid = #{userId}

mapper.update:
update member
set userpwd = #{userPwd},
	email = #{email},
	phone = #{phone},
	username = #{userName}
where userid = #{userId}

mapper.select:
select username, userid, email, joindate
from member
where userid = #{userid}
